import {useEffect, useState} from 'react';

import api from "./Api.js";

//import {Card, otherCard} from "./Card.js";

function handleEditAvatarClick(){document.querySelector('.popup_type-edit-avatar').classList.add('popup_open')};

function handleEditProfileClick(){document.querySelector('.popup_type-edit-profile').classList.add('popup_open')};

function handleAddPlaceClick(){document.querySelector('.popup_type-add-card').classList.add('popup_open')};

function Card (props){
  //console.log(props.link)
  return(
      <div className="template">
          <div className="card">
              <div className="card__rectangle">
                  <button className="card__delete-button" type="button"></button>
                  <div className="card__image-block">
                  <a href="#" className="card__open-fullsize-image">
                      <img src={props.link} className="card__image" alt="Card Name" />
                  </a>
                  </div>
                      <div className="card__title-block">
                      <h2 className="card__title">Card Name</h2>
                      <div className="card__like-section">
                          <button className="card__like-button" type="button"></button>
                          <p className="card__like-number">0</p>
                      </div>
                      </div>
                  </div>
              </div>
      </div>
  )
}





function Main(){

    const [apiRequest, setApiRequest] = useState([]);

    const [user, setUser] = useState([]);
    const [cards, setCards] = useState([]);

    useEffect(()=>{
      //handleUserRequest();
      handleCardsRequest();
    }, []);

    /*function handleUserRequest() {
      api.getUser()
      .then(data => {
          console.log(data);
              const userData = {
                id: item._id,
                name: item.name,
                about: item.about,
                avatar: item.avatar  
              }
          })
          setUser(userData);
        }
      }).catch(err => console.log(err))
    }*/

    function handleCardsRequest(){
      api.getCards()
        .then(data =>{
           //console.log(data);
           const cardsData = data.map(item=>{
              return{
                id: item._id,
                name: item.name,
                link: item.link,
                owner: item.owner,
                likes: item.likes
              }
           })
           setCards(cardsData);
        }).catch(err => console.log(err));
    }

      return(
      <main className="main">
            <section className="profile">
                <div className="profile__avatar">
                    <a href="#" className="profile__edit-avatar-link" onClick={handleEditAvatarClick}>
                      <img src="#" className="profile__image" alt="Фото профиля"/>
                      <div className="profile__avatar-overlay"></div>
                    </a>
                </div>
                <div className="profile__info">
                    <div className="profile__name-block">
                      <h1 className="profile__name"></h1>
                      <button className="profile__edit-button" type="button" onClick={handleEditProfileClick}></button>
                    </div>
                    <p className="profile__job"></p>
                </div>
                <button className="profile__add-button" type="button" onClick={handleAddPlaceClick}></button>
            </section>

          <section className="cards">
          {
              cards.map(item=>
                console.log(
                <Card
                  key={item.id}
                  link={item.link}
                />)
                )
          }
          </section>
        </main>
    )
};

export default Main;

